#include <vector>
#include <iostream>
using namespace std;

int majorityElement(const vector<int>& nums) {
    int count = 0, candidate = 0;

    // Finding the candidate
    for (int num : nums) {
        if (count == 0) {
            candidate = num;
            count = 1;
        } else if (num == candidate) {
            count++;
        } else {
            count--;
        }
    }

